import React from "react";
import propTypes from "prop-types";
import * as Styled from "./style";
import { TEXT_S, TEXT_M, TEXT_L } from "../../../public/font";

export const PSize = {
  s: TEXT_S,
  m: TEXT_M,
  l: TEXT_L,
};

export const PWeight = {
  xs: 100,
  s: 300,
  m: 500,
  l: 700,
};

const P = ({
  children = "",
  className = "",
  size = PSize.m,
  weight = PWeight.m,
}) => {
  const style = {
    fontSize: size,
    fontWeight: weight,
  };

  return (
    <Styled.P className={className} style={style}>
      {children}
    </Styled.P>
  );
};

P.propTypes = {
  children: propTypes.string,
  className: propTypes.string,
  size: propTypes.string,
  weight: propTypes.number,
};

export default P;
