import styled from "styled-components";
import { DARK_GRAY, MAIN_COLOR } from "../../../public/color";
import {} from "../../../public/font";

export const SignForm = styled.form`
  display: flex;
  width: 100%;
  flex-direction: column;
  align-items: center;
`;

export const InputContainer = styled.div`
  position: relative;
  width: 100%;
  & > label {
    display: inline-block;
    margin-bottom: 18px;
    line-height: 24px;
    font-weight: 500;
    color: ${DARK_GRAY};
  }
`;

export const CheckContainer = styled.div`
  display: flex;
  flex-direction: row;
  margin-bottom: 19px;
  opacity: ${(props) => (props.visible ? 1 : 0)};
`;

export const PasswordShowButton = styled.a`
  position: absolute;
  top: 50%;
  right: 0;
`;

export const CheckText = styled.p`
  position: relative;
  padding-left: 10px;
  margin-right: 44px;
  translate: all 0.3s ease;
  line-height: 28px;
  color: ${(props) => (props.checked ? `${MAIN_COLOR}` : `${WARNING}`)};
  &:last-child {
    margin-right: 0;
  }
  &:after {
    content: "";
    position: absolute;
    top: 50%;
    left: 0;
    transform: translateY(-50%);
    width: 5px;
    height: 5px;
    border-radius: 50%;
    background-color: ${(props) =>
      props.checked ? `${MAIN_COLOR}` : `${WARNING}`};
  }
`;
