import styled, { css } from "styled-components";
import { WHITE, MAIN_COLOR, DARK_GRAY } from "../../../public/color";
import { LeftOutlined, RightOutlined } from "@ant-design/icons";

export const TableContainer = styled.article`
  display: flex;
  flex-direction: column;
  width: 100%;
  min-height: 860px;
  background-color: ${WHITE};
  border-radius: 20px;
`;

const BoardButtonIcon = css`
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: ${WHITE};
  width: 30px;
  height: 30px;
  border-radius: 50%;
  box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.2);
`;

export const PreviousButton = styled(LeftOutlined)`
  ${BoardButtonIcon}
  margin-right: 2rem;
`;

export const NextButton = styled(RightOutlined)`
  ${BoardButtonIcon}
  margin-left: 2rem;
`;

export const CurrentPageButton = styled.button`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 30px;
  height: 30px;
  color: ${DARK_GRAY};
  background-color: none;

  ${(props) =>
    props.current &&
    css`
      color: ${WHITE};
      background-color: ${MAIN_COLOR};
      box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.2);
    `}
`;
