import React from "react";

import * as Styled from "./style";
import List, { ListDirection, ListAlignOrder } from "../../molecules/List";
import ListItem, {
  ItemSelfOrder,
  ItemJustifyOrder,
} from "../../molecules/ListItem";
import Title, { TitleSize, TitleWeight } from "../../atoms/Title";
import Span, { SpanType } from "../../atoms/Span";
import Button from "../../atoms/Button";

const dummyGroups = [
  {
    id: 1,
    title: "Web Frontend Study",
    mentor: "조재권",
    dDay: "D-1",
    heads: 12,
    maxHeads: 30,
    period: "20.03.16-20.06.21",
  },
  {
    id: 2,
    title: "Data structure",
    mentor: "이윤우",
    dDay: "D-1",
    heads: 7,
    maxHeads: 10,
    period: "20.03.16-20.06.20",
  },
  {
    id: 3,
    title: "Algorithm masters",
    mentor: "이현재",
    dDay: "D-21",
    heads: 21,
    maxHeads: 30,
    period: "20.03.16-20.12.11",
  },
  {
    id: 4,
    title: "Photoshop lecture",
    mentor: "잼누팍",
    dDay: "D-10",
    heads: 10,
    maxHeads: 20,
    period: "20.03.16-20.12.11",
  },
];

const Group = () => {
  let renderGroupItem = (groupList) =>
    groupList.map((data) => (
      <React.Fragment>
        <ListItem self={ItemSelfOrder.start}>
          <Styled.GroupState>{data.dDay}</Styled.GroupState>
        </ListItem>
        <ListItem>
          <Title size={TitleSize.s} weight={TitleWeight.l} marginBottom="1rem">
            {data.title}
          </Title>
        </ListItem>
        <ListItem width="100%">
          <Styled.GroupInformationList>
            <ListItem width="100%" justify={ItemJustifyOrder.between}>
              <Span type={SpanType.gray}>멘토</Span>
              <Span type={SpanType.gray} marginBottom="2rem">
                {data.mentor}
              </Span>
            </ListItem>
            <ListItem width="100%" justify={ItemJustifyOrder.between}>
              <Span type={SpanType.gray}>모집인원</Span>
              <Span type={SpanType.gray} marginBottom="2rem">
                {data.heads}/{data.maxHeads}
              </Span>
            </ListItem>
            <ListItem width="100%" justify={ItemJustifyOrder.between}>
              <Span type={SpanType.gray}>활동기간</Span>
              <Span type={SpanType.gray} marginBottom="2rem">
                {data.period}
              </Span>
            </ListItem>
          </Styled.GroupInformationList>
        </ListItem>

        <ListItem>
          <Button width="200px" height="40px">
            지원하기
          </Button>
        </ListItem>
      </React.Fragment>
    ));

  return (
    <div>
      <List
        card={true}
        direction={ListDirection.col}
        align={ListAlignOrder.center}
        width="33%"
      ></List>
    </div>
  );
};

export default Group;
