const postReducer = (prevState = initialState, action) => {
  return produce(prevState, (draft) => {
    switch (action.type) {
      case LOAD_POST_LISTS_REQUEST:
        draft.isLoadedPostLists = false;
        draft.isLoadingPostLists = true;
        break;
      case LOAD_POST_LISTS_SUCCESS:
        draft.isLoadedPostLists = true;
        draft.isLoadingPostLists = false;
        let col = 0;
        draft.postLists = Array(Math.ceil(dummyPosts.length / 20))
          .fill(null)
          .map(() => Array());
        dummyPosts.forEach((post, i) => {
          if (i % 20 === 0 && i !== 0) {
            col++;
          }
          draft.postLists[col].push({
            id: post.id,
            title: post.title,
            user: post.user,
            date: post.date,
          });
        });
        break;
      case LOAD_POST_LISTS_FAILURE:
        draft.isLoadedPostLists = false;
        draft.isLoadingPostLists = false;
        break;
      case LOAD_POST_REQUEST:
        draft.isLoadedPost = false;
        draft.isLoadingPost = true;
        break;
      case LOAD_POST_SUCCESS:
        draft.isLoadedPost = true;
        draft.isLoadingPost = false;
        draft.post = dummyPosts[dummyPosts.findIndex((v) => v.id == action.id)];
        break;
      case LOAD_POST_FAILURE:
        draft.isLoadedPost = false;
        draft.isLoadingPost = false;
        break;
      case ADD_POST_REQUEST:
        draft.isAddingPost = true;
        draft.isAddedPost = false;
        draft.addPostErrorReason = "";
        break;
      case ADD_POST_SUCCESS:
        draft.isAddingPost = false;
        draft.isAddedPost = true;
        break;
      case ADD_POST_FAILURE:
        draft.isAddingPost = false;
        draft.isAddedPost = false;
        draft.addPostErrorReason = action.error;
        break;
      case ADD_COMMENT_REQUEST:
        draft.isAddingComment = true;
        draft.isAddedComment = false;
        draft.addCommentErrorReason = "";
        break;
      case ADD_COMMENT_SUCCESS:
        draft.isAddingComment = false;
        draft.isAddedComment = true;
        const postIndex = dummyPosts.findIndex(
          (v) => v.id == action.data.postId
        );
        console.log(postIndex);
        // draft.postLists[
        //   draft.postLists.findIndex((v) => v.id === action.data.postId)
        // ].comments.push(action.data);
        break;
      case ADD_COMMENT_FAILURE:
        draft.isAddingComment = false;
        draft.isAddedComment = false;
        draft.addCommentErrorReason = action.error;
        break;
      default:
        break;
    }
  });
};

export default postReducer;
