import React, { useState, useEffect } from "react";
import propTypes from "prop-types";
import { useDispatch, useSelector } from "react-redux";
import Router, { useRouter } from "next/router";

import * as Styled from "./style";
import Title, { TitleSize, TitleWeight } from "../../atoms/Title";
import Table from "../../molecules/Table";
import Box, { BoxSort } from "../../molecules/Box";
import Span from "../../atoms/Span";
import Button, { ButtonFontSize } from "../../atoms/Button";
import { LOAD_POST_LISTS_REQUEST } from "../../../reducers/post";

const HeadData = [
  { name: "번호", width: "auto" },
  { name: "제목", width: "50%" },
  { name: "작성자", width: "auto" },
  { name: "날짜", width: "auto" },
];

const Board = () => {
  const [currentIndex, setCurrentIndex] = useState(1);
  const dispatch = useDispatch();
  const { postLists, isLoadedPostLists } = useSelector(
    (state) => state.postReducer
  );

  useEffect(() => {
    dispatch({ type: LOAD_POST_LISTS_REQUEST });

    // let col = 0;
    // posts = Array(Math.ceil(postLists.length / 20))
    //   .fill(null)
    //   .map(() => Array());
    // postLists.map((post, i) => {
    //   if (i % 20 === 0 && i !== 0) {
    //     col++;
    //   }
    //   posts[col].push(post);
    // });
  }, []);

  const renderPageIndexButtons = () => {
    return postLists.map((post, i) =>
      i === currentIndex - 1 ? (
        <Styled.CurrentPageButton key={post.id} current>
          {currentIndex}
        </Styled.CurrentPageButton>
      ) : (
        <Styled.CurrentPageButton key={post.id}>
          {currentIndex + i}
        </Styled.CurrentPageButton>
      )
    );
  };

  return (
    <React.Fragment>
      {console.log(postLists[1])}
      <Box sort={BoxSort.CENTER_LEFT}>
        <Title size={TitleSize.xs} weight={TitleWeight.l} mar={[2, 0, 2, 2]}>
          게시판
        </Title>
      </Box>
      <Box sort={BoxSort.TOP_RIGHT} mar={[0, 0, 2, 0]}>
        <Button width="100px" height="34px" fontSize={ButtonFontSize.S}>
          글쓰기
        </Button>
      </Box>
      {isLoadedPostLists && (
        <Styled.TableContainer>
          <Table headData={HeadData} bodyData={postLists} />
        </Styled.TableContainer>
      )}

      <Box sort={BoxSort.CENTER_CENTER} mar={[2, 0]}>
        <Styled.PreviousButton />
        {renderPageIndexButtons()}
        <Span mar={[0, 1.5, 0, 0]}>...</Span>
        <Styled.CurrentPageButton>23</Styled.CurrentPageButton>
        <Styled.NextButton />
      </Box>
    </React.Fragment>
  );
};

Board.propTypes = {};

export default Board;
