import React from "react";
import propTypes from "prop-types";

import * as Styled from "./style";

export const Type = {
  bg: "background",
  img: "image",
};

const Image = ({
  style: { circle = false },
  option: { src = "", type = Type.img },
}) => {
  const srcPath = "/assets/images/" + option.type.src;

  return (
    <>
      {option.type === Type.img ? (
        <Styled.Image src={srcPath} circle={circle} />
      ) : (
        <Styled.Background src={srcPath}></Styled.Background>
      )}
    </>
  );
};

Image.propTypes = {
  option: propTypes.shape({
    src: propTypes.string.isRequired,
    type: propTypes.string,
  }),
  style: propTypes.shape({
    circle: propTypes.bool,
  }),
};

export default Image;
