import React from "react";
import propTypes from "prop-types";
import * as Styled from "./style";
import {
  WEIGHT_S,
  WEIGHT_M,
  WEIGHT_L,
  WEIGHT_XL,
  TEXT_S,
  TEXT_M,
  TEXT_L,
} from "../../../assets/font";
import { BLACK, MAIN_COLOR, DARK_GRAY, WARNING } from "../../../assets/color";

export const SpanType = {
  normal: BLACK,
  primary: MAIN_COLOR,
  gray: DARK_GRAY,
  warn: WARNING,
};

export const SpanSize = {
  s: TEXT_S,
  m: TEXT_M,
  l: TEXT_L,
};

export const SpanWeight = {
  s: WEIGHT_S,
  m: WEIGHT_M,
  l: WEIGHT_L,
  xl: WEIGHT_XL,
};

const Span = ({
  children = "",
  size = SpanSize.m,
  weight = SpanWeight.m,
  type = SpanType.normal,
  marginBottom = "0px",
}) => {
  return (
    <Styled.Span
      size={size}
      weight={weight}
      type={type}
      marginBottom={marginBottom}
    >
      {children}
    </Styled.Span>
  );
};

Span.propTypes = {
  children: propTypes.node.isRequired,
  size: propTypes.string,
  weight: propTypes.number,
  type: propTypes.string,
  marginBottom: propTypes.string,
};

export default Span;
