import styled, { css } from "styled-components";

export const ListItem = styled.li`
  display: flex;
  width: 100%;
  flex-direction: ${(props) => props.style.direction === "row" && row};
  align-items: ${(props) => props.style.align};
  justify-content: ${(props) => props.style.justify};
  align-self: ${(props) => props.style.self};
  margin-right: ${(props) =>
    props.style.direction === "row" ? props.style.margin : "0"};
  margin-bottom: ${(props) =>
    props.style.direction === "column" ? props.style.margin : "0"};
  &:last-child {
    margin: 0;
  }
`;
