import styled, { css } from "styled-components";

export const ListItem = styled.li`
  display: flex;
  width: 100%;
  align-items: ${(props) => props.style.align};
  justify-content: ${(props) => props.style.justify};
  align-self: ${(props) => props.style.self};
  margin-left: ${(props) =>
    props.style.direction === "row" ? props.style.marg : "0"};
  margin-left: ${(props) =>
    props.style.direction === "col" ? props.style.marg : "0"};
`;
