import styled from "styled-components";
import { BLACK, DARK_GRAY, MAIN_COLOR } from "../../../public/color";
import { TEXT_M, TEXT_L } from "../../../public/font";

const NOTICE_IMAGE_WIDTH = "336px";
const NOTICE_IMAGE_HEIGHT = "472px";

const INTERVAL = "30px";

export const Notice = styled.div`
  display: flex;
  flex-direction: row;
  width: 100%;
`;

export const ContentTitle = styled.h2`
  align-self: flex-start;
  font-weight: 700;
  color: ${BLACK};
  font-size: ${TEXT_M};
  padding: 20px 0 20px 20px;
  margin: 0;
`;

export const NoticeImageContainer = styled.div`
  flex: 0 0 ${NOTICE_IMAGE_WIDTH};
  height: ${NOTICE_IMAGE_HEIGHT};
  overflow: hidden;
  border-radius: 10px;
  margin-right: ${INTERVAL};
`;

// export const NoticeImage = styled.img.attrs({
//   src: NoticePromotion,
//   alt: "Notice Promotion Image",
// })`
//   width: 100%;
//   height: 100%;
// `;

export const NoticeInformationCard = styled.div`
  ${Card}
  ${CardShadow}
  position: relative;
  width: calc(100% - (${NOTICE_IMAGE_WIDTH} + ${INTERVAL}));
  max-height: ${NOTICE_IMAGE_HEIGHT};
`;

export const NoticeInformationTitle = styled.h3`
  width: 100%;
  padding-bottom: 20px;
  font-size: ${TEXT_L};
  font-weight: 500;
  color: ${BLACK};
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
`;

export const NoticeInformationText = styled.p`
  margin: 18px 0;
  overflow-y: scroll;
  color: ${DARK_GRAY};
  font-size: ${TEXT_M};
  & > a {
    color: ${MAIN_COLOR};
  }
  /* -ms-overflow-style: none;
  scrollbar-width: none;
  ::-webkit-scrollbar {
    display: none;
  } */
`;

export const JoinButton = styled(Button)`
  align-self: center;
  width: ${(props) => props.width};
  line-height: ${(props) => props.height};
`;
