import React from "react";
import propTypes from "prop-types";

import * as Styled from "./style";

export const Type = {
  bg: "background",
  img: "image",
};

const Image = ({
  style = { circle: false, marginBottom: "0px" },
  option = { src: "", type: Type.img },
}) => {
  const srcPath = "/assets/images/" + option.type.src;

  return (
    <>
      {option.type === Type.img ? (
        <Styled.Image style={style} src={srcPath} />
      ) : (
        <Styled.Background style={style} src={srcPath}></Styled.Background>
      )}
    </>
  );
};

Image.propTypes = {
  option: propTypes.shape({
    src: propTypes.string.isRequired,
    type: propTypes.string,
  }),
  style: propTypes.shape({
    circle: propTypes.bool,
    marginBottom: propTypes.string,
  }),
};

export default Image;
